# Copyright (c) 1999-2000 by Secret Labs AB

# load a unicode-data file from disk

class UnicodeData:
    # Record structure:
    # [ID, name, category, combining, bidi, decomp,  (6)
    #  decimal, digit, numeric, bidi-mirrored, Unicode-1-name, (11)
    #  ISO-comment, uppercase, lowercase, titlecase, ea-width, (16)
    #  derived-props] (17)

    def __init__(self, version,
                 linebreakprops=False,
                 expand=1,
                 cjk_check=True,
                 select=lambda c: True):
        self.changed = []
        table = [None] * 0x110000
        with open_data(UNICODE_DATA, version) as file:
            while 1:
                s = file.readline()
                if not s:
                    break
                s = s.strip().split(";")
                char = int(s[0], 16)
                if select(char):
                    table[char] = s

        cjk_ranges_found = []
        cjk_ranger = [(a,b) for a,b in cjk_ranges
                      if select(int(a,16)) and select(int(b,16))]

        # expand first-last ranges
        if expand:
            field = None
            for i in range(0, 0x110000):
                if not select(i):
                    continue
                s = table[i]
                if s:
                    if s[1][-6:] == "First>":
                        s[1] = ""
                        field = s
                    elif s[1][-5:] == "Last>":
                        if s[1].startswith("<CJK Ideograph"):
                            cjk_ranges_found.append((field[0],
                                                     s[0]))
                        s[1] = ""
                        field = None
                elif field:
                    f2 = field[:]
                    f2[0] = "%X" % i
                    table[i] = f2
            # if cjk_check and cjk_ranger != cjk_ranges_found:
            #     raise ValueError("CJK ranges deviate: have %r want %r" %
            #                      (cjk_ranges_found, cjk_ranger))

        # public attributes
        self.filename = UNICODE_DATA % ''
        self.table = table
        self.chars = list(range(0x110000)) # unicode 3.2

        # check for name aliases and named sequences, see #12753
        # aliases and named sequences are not in 3.2.0
        if version != '3.2.0':
            self.aliases = []
            # store aliases in the Private Use Area 15, in range U+F0000..U+F00FF,
            # in order to take advantage of the compression and lookup
            # algorithms used for the other characters
            pua_index = NAME_ALIASES_START
            with open_data(NAME_ALIASES, version) as file:
                for s in file:
                    s = s.strip()
                    if not s or s.startswith('#'):
                        continue
                    char, name, abbrev = s.split(';')
                    char = int(char, 16)
                    if select(pua_index) and select(char):
                        self.aliases.append((name, char))
                        # also store the name in the PUA 1
                        self.table[pua_index][1] = name
                        pua_index += 1
            assert pua_index - NAME_ALIASES_START == len(self.aliases)

            self.named_sequences = []
            # store named sequences in the PUA 1, in range U+F0100..,
            # in order to take advantage of the compression and lookup
            # algorithms used for the other characters.

            assert pua_index < NAMED_SEQUENCES_START
            pua_index = NAMED_SEQUENCES_START
            if select(pua_index):
                with open_data(NAMED_SEQUENCES, version) as file:
                    for s in file:
                        s = s.strip()
                        if not s or s.startswith('#'):
                            continue
                        name, chars = s.split(';')
                        chars = tuple(int(char, 16) for char in chars.split())
                        chars = tuple(c for c in chars if select(c))
                        # check that the strutcure defined in makeunicodename is OK
                        assert 2 <= len(chars) <= 4, "change the Py_UCS2 array size"
                        assert all(c <= 0xFFFF for c in chars), ("use Py_UCS4 in "
                            "the NamedSequence struct and in unicodedata_lookup")
                        self.named_sequences.append((name, chars))
                        # also store these in the PUA 1
                        self.table[pua_index][1] = name
                        pua_index += 1
                assert pua_index - NAMED_SEQUENCES_START == len(self.named_sequences)

        self.exclusions = {}
        with open_data(COMPOSITION_EXCLUSIONS, version) as file:
            for s in file:
                s = s.strip()
                if not s:
                    continue
                if s[0] == '#':
                    continue
                char = int(s.split()[0],16)
                if select(char):
                    self.exclusions[char] = 1

        widths = [None] * 0x110000
        with open_data(EASTASIAN_WIDTH, version) as file:
            for s in file:
                s = s.strip()
                if not s:
                    continue
                if s[0] == '#':
                    continue
                s = s.split()[0].split(';')
                if '..' in s[0]:
                    first, last = [int(c, 16) for c in s[0].split('..')]
                    chars = list(range(first, last+1))
                else:
                    chars = [int(s[0], 16)]
                for char in chars:
                    if select(char):
                        widths[char] = s[1]

        for i in range(0, 0x110000):
            if table[i] is not None:
                table[i].append(widths[i])

        for i in range(0, 0x110000):
            if table[i] is not None:
                table[i].append(set())

        with open_data(DERIVED_CORE_PROPERTIES, version) as file:
            for s in file:
                s = s.split('#', 1)[0].strip()
                if not s:
                    continue
                r, p = s.split(";")
                r = r.strip()
                p = p.strip()
                if ".." in r:
                    first, last = [int(c, 16) for c in r.split('..')]
                    chars = list(range(first, last+1))
                else:
                    chars = [int(r, 16)]
                for char in chars:
                    if table[char]:
                        # Some properties (e.g. Default_Ignorable_Code_Point)
                        # apply to unassigned code points; ignore them
                        table[char][-1].add(p)

        with open_data(LINE_BREAK, version) as file:
            for s in file:
                s = s.partition('#')[0]
                s = [i.strip() for i in s.split(';')]
                if len(s) < 2 or s[1] not in MANDATORY_LINE_BREAKS:
                    continue
                if '..' not in s[0]:
                    first = last = int(s[0], 16)
                else:
                    first, last = [int(c, 16) for c in s[0].split('..')]
                for char in range(first, last+1):
                    if select(char):
                        table[char][-1].add('Line_Break')

        # We only want the quickcheck properties
        # Format: NF?_QC; Y(es)/N(o)/M(aybe)
        # Yes is the default, hence only N and M occur
        # In 3.2.0, the format was different (NF?_NO)
        # The parsing will incorrectly determine these as
        # "yes", however, unicodedata.c will not perform quickchecks
        # for older versions, and no delta records will be created.
        quickchecks = [0] * 0x110000
        qc_order = 'NFD_QC NFKD_QC NFC_QC NFKC_QC'.split()
        with open_data(DERIVEDNORMALIZATION_PROPS, version) as file:
            for s in file:
                if '#' in s:
                    s = s[:s.index('#')]
                s = [i.strip() for i in s.split(';')]
                if len(s) < 2 or s[1] not in qc_order:
                    continue
                quickcheck = 'MN'.index(s[2]) + 1 # Maybe or No
                quickcheck_shift = qc_order.index(s[1])*2
                quickcheck <<= quickcheck_shift
                if '..' not in s[0]:
                    first = last = int(s[0], 16)
                else:
                    first, last = [int(c, 16) for c in s[0].split('..')]
                for char in range(first, last+1):
                    if select(char):
                        assert not (quickchecks[char]>>quickcheck_shift)&3
                        quickchecks[char] |= quickcheck
        for i in range(0, 0x110000):
            if table[i] is not None:
                table[i].append(quickchecks[i])

        with open_data(UNIHAN, version) as file:
            zip = zipfile.ZipFile(file)
            if version == '3.2.0':
                data = zip.open('Unihan-3.2.0.txt').read()
            else:
                data = zip.open('Unihan_NumericValues.txt').read()
        for line in data.decode("utf-8").splitlines():
            if not line.startswith('U+'):
                continue
            code, tag, value = line.split(None, 3)[:3]
            if tag not in ('kAccountingNumeric', 'kPrimaryNumeric',
                           'kOtherNumeric'):
                continue
            value = value.strip().replace(',', '')
            i = int(code[2:], 16)
            # Patch the numeric field
            if table[i] is not None:
                table[i][8] = value
        sc = self.special_casing = {}
        with open_data(SPECIAL_CASING, version) as file:
            for s in file:
                s = s[:-1].split('#', 1)[0]
                if not s:
                    continue
                data = s.split("; ")
                if data[4]:
                    # We ignore all conditionals (since they depend on
                    # languages) except for one, which is hardcoded. See
                    # handle_capital_sigma in unicodeobject.c.
                    continue
                c = int(data[0], 16)
                if select(c):
                    lower = [int(char, 16) for char in data[1].split() if select(int(char, 16))]
                    title = [int(char, 16) for char in data[2].split() if select(int(char, 16))]
                    upper = [int(char, 16) for char in data[3].split() if select(int(char, 16))]
                    sc[c] = (lower, title, upper)
        cf = self.case_folding = {}
        if version != '3.2.0':
            with open_data(CASE_FOLDING, version) as file:
                for s in file:
                    s = s[:-1].split('#', 1)[0]
                    if not s:
                        continue
                    data = s.split("; ")
                    if data[1] in "CF":
                        c = int(data[0], 16)
                        if select(c):
                            cf[c] = [int(char, 16) for char in data[2].split()]

    def uselatin1(self):
        # restrict character range to ISO Latin 1
        self.chars = list(range(256))

# hash table tools

# this is a straight-forward reimplementation of Python's built-in
# dictionary type, using a static data structure, and a custom string
# hash algorithm.

def myhash(s, magic):
    h = 0
    for c in map(ord, s.upper()):
        h = (h * magic) + c
        ix = h & 0xff000000
        if ix:
            h = (h ^ ((ix>>24) & 0xff)) & 0x00ffffff
    return h

SIZES = [
    (4,3), (8,3), (16,3), (32,5), (64,3), (128,3), (256,29), (512,17),
    (1024,9), (2048,5), (4096,83), (8192,27), (16384,43), (32768,3),
    (65536,45), (131072,9), (262144,39), (524288,39), (1048576,9),
    (2097152,5), (4194304,3), (8388608,33), (16777216,27)
]

class Hash:
    def __init__(self, name, data, magic):
        # turn a (key, value) list into a static hash table structure

        # determine table size
        for size, poly in SIZES:
            if size > len(data):
                poly = size + poly
                break
        else:
            raise AssertionError("ran out of polynomials")

        print(size, "slots in hash table")

        table = [None] * size

        mask = size-1

        n = 0

        hash = myhash

        # initialize hash table
        for key, value in data:
            h = hash(key, magic)
            i = (~h) & mask
            v = table[i]
            if v is None:
                table[i] = value
                continue
            incr = (h ^ (h >> 3)) & mask;
            if not incr:
                incr = mask
            while 1:
                n = n + 1
                i = (i + incr) & mask
                v = table[i]
                if v is None:
                    table[i] = value
                    break
                incr = incr << 1
                if incr > mask:
                    incr = incr ^ poly

        print(n, "collisions")
        self.collisions = n

        for i in range(len(table)):
            if table[i] is None:
                table[i] = 0

        self.data = Array(name + "Hash", table, pack=True)
        self.magic = magic
        self.name = name
        self.size = size
        self.poly = poly

    def dump(self, file, hdr, trace):
        # write data to file, as a C array
        self.data.dump(file, hdr, trace)
        hdr.write("#define %sMagic %d\n" % (self.name, self.magic))
        hdr.write("#define %sSize %d\n" % (self.name, self.size))
        hdr.write("#define %sPoly %d\n" % (self.name, self.poly))

# stuff to deal with arrays of unsigned integers

def pack(data, bits, word=32):
    assert 0 < bits < word
    bitn = (bits * len(data) + word - 1) // word
    bita = 0
    for x in reversed(data):
        bita <<= bits
        bita |= x
    for i in range(bitn):
        yield bita & ((1 << word) - 1)
        bita >>= 32

def spack(data, bits, word=32):
    assert 0 < bits < word
    bitn = (bits * len(data) + word - 1) // word
    bita = 0
    sign = 1 << (bits - 1)
    mask = sign - 1
    for x in reversed(data):
        assert -sign <= x < sign, "x=%d bits=%d" % (x, bits)
        x = (x & mask) | (sign if x < 0 else 0)
        bita <<= bits
        bita |= x
    for i in range(bitn):
        yield bita & ((1 << word) - 1)
        bita >>= 32

def bzip(data):
    return bz2.compress(data)

def deflate(data):
    # z = zlib.compressobj(zlib.Z_BEST_COMPRESSION, zlib.DEFLATED, -zlib.MAX_WBITS, zlib.DEF_MEM_LEVEL, zlib.Z_RLE)
    z = zlib.compressobj(zlib.Z_BEST_COMPRESSION, zlib.DEFLATED, -zlib.MAX_WBITS)
    b = z.compress(data)
    b += z.flush(zlib.Z_FINISH)
    return b

def rle(data, maxval):
    i = 0
    j = 0
    for i,x in enumerate(data):
        if j == 0:
            y = x
            j = 1
        elif y == x and j < maxval:
            j += 1
        else:
            yield (j, y)
            y = x
            j = 1
    if j:
        yield (j, y)

def uleb(a, x):
    while True:
        b = x & 127
        x >>= 7
        if x:
            a.append(b | 128)
        else:
            a.append(b)
            break

def zig(x):
    m = (2 << x.bit_length()) - 1
    return ((x & (m >> 1)) << 1) ^ (m if x < 0 else 0)

def zleb(a, x):
    return uleb(a, zig(x))

def sleb(a, x):
    t = 0
    while not t:
        b = x & 127
        x >>= 7
        if (x == 0 and not (b & 64)) or (x == -1 and (b & 64)):
            t = 1
        else:
            b |= 128
        a.append(b)

def leb(data):
    i = 0
    p = 0
    a = bytearray()
    for x in data:
        sleb(a, x - p)
        p = x
    return a

def zd(data):
    n = 0;
    i = 0
    p = 0
    a = bytearray()
    for x in data:
        zleb(a, x - p)
        p = x
    return deflate(a), len(a)

def com(x):
    return '{:,}'.format(x)

class Array:
    def __init__(self, name, data, rle=False, pack=False, zd=False):
        self.name = name
        self.data = data
        self.pack = pack
        self.rle = rle  # adds 90s latency to startup
        self.zd = zd

    def to_bytes(self, size, order):
        return b''.join(i.to_bytes(size, order) for i in self.data)

    def dump(self, file, hdr, trace=0):
        # write data to f, as a C array
        f = file
        bits = max(x.bit_length() for x in self.data)
        size = getsize(self.data)
        if trace:
            print("%s: %d bits" % (self.name, bits))
            print("%s: size         is %12s bytes" % (self.name, com(size*len(self.data))))
            print("%s: packed size  is %12s bytes" % (self.name, com((bits*len(self.data)+31)//32*4)))
            print("%s: rle size     is %12s bytes" % (self.name, com(len(tuple(rle(self.data, (1<<(8*size))-1)))*size*2)))
            print("%s: deflate size is %12s bytes" % (self.name, com(len(deflate(self.to_bytes(size, 'little'))))))
            print("%s: bz2 size     is %12s bytes" % (self.name, com(len(bzip(self.to_bytes(size, 'little'))))))
            print("%s: leb size    is %12s bytes" % (self.name, com(len(leb(self.data)))))
            print("%s: zd size     is %12s bytes" % (self.name, com(len(zd(self.data)[0]))))
        if self.pack:
            hdr.write("#define %sBits %d\n" % (self.name, bits))
            self.data = tuple(pack(self.data, bits))
            size = 4
        if self.zd:
            m = size
            self.data, n = zd(self.data)
            size = 1
        if size == 1:
            t = "unsigned char"
        elif size == 2:
            t = "unsigned short"
        else:
            t = "unsigned int"
        hdr.write("extern const %s %s[%d];\n" % (t, self.name, len(self.data)))
        if self.rle:
            codes = tuple(rle(self.data, (1<<(8*size))-1))
            f.write("%s %s[%d];\n" % (t, self.name, len(self.data)))
            f.write("static const %s %s_rodata[%d+1][2] = { /* %g%% profit */\n" % (t, self.name, len(codes), len(codes) * size * 2 / float(len(self.data) * size) * 100))
            for a,b in codes:
                f.write("    {%3d, 0x%02x},\n" % (a, b))
            f.write("    {0},\n")
            f.write("};\n")
            f.write("static textstartup void %s_init(void) {\n" % (self.name));
            if size == 1:
                f.write("    rldecode2(%s, (void *)%s_rodata);\n" % (self.name, self.name));
            else:
                f.write("    int i, j, k;\n");
                f.write("    for (k = i = 0; i < %d; ++i) {\n" % (len(codes)));
                f.write("        for (j = 0; j < %s_rodata[i][0]; ++j) {\n" % (self.name));
                f.write("            %s[k++] = %s_rodata[i][1];\n" % (self.name, self.name));
                f.write("        }\n");
                f.write("    }\n");
            f.write("}\n");
            f.write("const void *const %s_ctor[] initarray = {\n" % (self.name));
            f.write("    %s_init,\n" % (self.name));
            f.write("};\n");
            f.write("\n");
        else:
            f.write("const %s %s[%d] = {\n" % (t, self.name, len(self.data)))
            if self.data:
                s = "    "
                for item in self.data:
                    i = str(item) + ", "
                    if len(s) + len(i) > 78:
                        f.write(s + "\n")
                        s = "    " + i
                    else:
                        s = s + i
                if s.strip():
                    f.write(s + "\n")
            f.write("};\n\n")
            if self.zd:
                f.write("/* %d %d */\n" % (n, m))

def getsize(data):
    # return smallest possible integer size for the given array
    maxdata = max(data)
    if maxdata < 256:
        return 1
    elif maxdata < 65536:
        return 2
    else:
        return 4

def splitbins(t, trace=0):
    """t, trace=0 -> (t1, t2, shift).  Split a table to save space.

    t is a sequence of ints.  This function can be useful to save space if
    many of the ints are the same.  t1 and t2 are lists of ints, and shift
    is an int, chosen to minimize the combined size of t1 and t2 (in C
    code), and where for each i in range(len(t)),
        t[i] == t2[(t1[i >> shift] << shift) + (i & mask)]
    where mask is a bitmask isolating the last "shift" bits.

    If optional arg trace is non-zero (default zero), progress info
    is printed to sys.stderr.  The higher the value, the more info
    you'll get.
    """
    if trace:
        def dump(t1, t2, shift, bytes):
            print("%d+%d bins at shift %d; %d bytes" % (
                len(t1), len(t2), shift, bytes))
        print("Size of original table:", len(t)*getsize(t), \
                            "bytes")
    n = len(t)-1    # last valid index
    maxshift = 0    # the most we can shift n and still have something left
    if n > 0:
        while n >> 1:
            n >>= 1
            maxshift += 1
    del n
    bytes = sys.maxsize  # smallest total size so far
    t = tuple(t)    # so slices can be dict keys
    for shift in range(maxshift + 1):
        t1 = []
        t2 = []
        size = 2**shift
        bincache = {}
        for i in range(0, len(t), size):
            bin = t[i:i+size]
            index = bincache.get(bin)
            if index is None:
                index = len(t2)
                bincache[bin] = index
                t2.extend(bin)
            t1.append(index >> shift)
        # determine memory size
        b = len(t1)*getsize(t1) + len(t2)*getsize(t2)
        if trace > 1:
            dump(t1, t2, shift, b)
        if b < bytes:
            best = t1, t2, shift
            bytes = b
    t1, t2, shift = best
    if trace:
        print("Best:", end=' ')
        dump(t1, t2, shift, bytes)
    if __debug__:
        # exhaustively verify that the decomposition is correct
        mask = ~((~0) << shift) # i.e., low-bit mask of shift bits
        for i in range(len(t)):
            assert t[i] == t2[(t1[i >> shift] << shift) + (i & mask)]
    return best

